import React, { useState } from 'react';
import axios from 'axios';

const ApplicationForm = ({ onSuccess }) => {
  const [formData, setFormData] = useState({
    company: '',
    position: '',
    status: '',
    contact_person: '',
    email: '',
    phone: '',
    date_applied: '',
    notes: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Sende die Formulardaten an das Backend
      await axios.post('http://localhost:3001/applications', formData);
      alert('Bewerbung erfolgreich hinzugefügt!');
      setFormData({
        company: '',
        position: '',
        status: '',
        contact_person: '',
        email: '',
        phone: '',
        date_applied: '',
        notes: '',
      });
      onSuccess(); // Lädt die Liste der Bewerbungen neu
    } catch (error) {
      console.error('Fehler beim Hinzufügen:', error);
      alert('Fehler beim Hinzufügen der Bewerbung.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Neue Bewerbung hinzufügen</h2>
      <input
        type="text"
        name="company"
        value={formData.company}
        placeholder="Firma"
        onChange={handleChange}
        required
      />
      <br />
      <input
        type="text"
        name="position"
        value={formData.position}
        placeholder="Position"
        onChange={handleChange}
        required
      />
      <br />
      <select
        name="status"
        value={formData.status}
        onChange={handleChange}
        required
      >
        <option value="">Status auswählen</option>
        <option value="offen">Offen</option>
        <option value="Interview">Interview</option>
        <option value="abgelehnt">Abgelehnt</option>
      </select>
      <br />
      <input
        type="text"
        name="contact_person"
        value={formData.contact_person}
        placeholder="Ansprechpartner"
        onChange={handleChange}
      />
      <br />
      <input
        type="email"
        name="email"
        value={formData.email}
        placeholder="E-Mail"
        onChange={handleChange}
      />
      <br />
      <input
        type="tel"
        name="phone"
        value={formData.phone}
        placeholder="Telefonnummer"
        onChange={handleChange}
      />
      <br />
      <input
        type="date"
        name="date_applied"
        value={formData.date_applied}
        onChange={handleChange}
      />
      <br />
      <textarea
        name="notes"
        value={formData.notes}
        placeholder="Notizen"
        onChange={handleChange}
      ></textarea>
      <br />
      <button type="submit">Hinzufügen</button>
    </form>
  );
};

export default ApplicationForm;
