import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ApplicationForm = ({ onSuccess }) => {
  const [formData, setFormData] = useState({
    company: '',
    position: '',
    status: '',
    contact_person: '',
    email: '',
    phone: '',
    date_applied: '',  // Das Datum bleibt leer, wird später gesetzt
    notes: '',
  });
  const [coverLetter, setCoverLetter] = useState(null);
  const [jobPosting, setJobPosting] = useState(null);

  // Effect Hook, um das Datum des Formulars auf den heutigen Tag zu setzen
  useEffect(() => {
    const today = new Date().toISOString().split('T')[0]; // Setzt das Datum auf "YYYY-MM-DD"
    setFormData((prev) => ({ ...prev, date_applied: today }));
  }, []); // Effekt nur beim ersten Rendern ausführen

  // handleChange: Aktualisiert den FormState basierend auf den Benutzereingaben
  const handleChange = (e) => {
    const { name, value } = e.target;
    console.log(`Field ${name} changed to: ${value}`);  // Debug: Log für jede Feldänderung
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // handleFileChange: Speichert die ausgewählten Dateien im State
  const handleFileChange = (e) => {
    const { name, files } = e.target;
    console.log(`File ${name} selected: ${files[0]?.name}`);  // Debug: Log für jede Dateiänderung
    if (name === 'coverLetter') {
      setCoverLetter(files[0]);
    } else if (name === 'jobPosting') {
      setJobPosting(files[0]);
    }
  };

  // handleSubmit: Validiert die Formulardaten und sendet sie an das Backend
  const handleSubmit = (e) => {
    e.preventDefault();

    // Einfache Alert, um sicherzustellen, dass der Handler ausgelöst wird
    alert("Formular abgeschickt!");

    // Debug: Zeigt die Formulardaten vor dem Absenden an
    console.log('Form Data before submit:', formData);
    console.log('Cover Letter:', coverLetter);
    console.log('Job Posting:', jobPosting);

    // Überprüfen, ob alle erforderlichen Felder ausgefüllt sind
    if (!formData.company || !formData.position || !formData.status || !formData.contact_person || !formData.email || !formData.phone || !formData.date_applied) {
      alert('Alle erforderlichen Felder müssen ausgefüllt werden!');
      return;
    }

    const formDataToSend = new FormData();
    formDataToSend.append('company', formData.company);
    formDataToSend.append('position', formData.position);
    formDataToSend.append('status', formData.status); // Stellt sicher, dass `status` übermittelt wird
    formDataToSend.append('contact_person', formData.contact_person);
    formDataToSend.append('email', formData.email);
    formDataToSend.append('phone', formData.phone);
    formDataToSend.append('date_applied', formData.date_applied); // Überprüfen, ob `date_applied` korrekt übermittelt wird
    formDataToSend.append('notes', formData.notes);

    if (coverLetter) formDataToSend.append('coverLetter', coverLetter);
    if (jobPosting) formDataToSend.append('jobPosting', jobPosting);

    // Sende die Formulardaten an den Server
    axios
      .post('http://localhost:3001/applications', formDataToSend)
      .then((response) => {
        alert('Bewerbung erfolgreich hinzugefügt!');
        onSuccess(); // Callback, um die Bewerbungsliste neu zu laden
        setFormData({
          company: '',
          position: '',
          status: '',
          contact_person: '',
          email: '',
          phone: '',
          date_applied: '', // Datum zurücksetzen
          notes: '',
        });
        setCoverLetter(null);
        setJobPosting(null);
      })
      .catch((error) => {
        alert('Fehler beim Hochladen: ' + error.message);
      });
  };

  // Der Return-Bereich: Hier befinden sich die Formularfelder
  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Firma:</label>
        <input
          type="text"
          name="company"
          value={formData.company}
          onChange={handleChange}
        />
      </div>
      <div>
        <label>Position:</label>
        <input
          type="text"
          name="position"
          value={formData.position}
          onChange={handleChange}
        />
      </div>
      <div>
        <label>Status:</label>
        <select
          name="status"
          value={formData.status}
          onChange={handleChange}
        >
          <option value="">Wählen Sie den Status</option>
          <option value="offen">Offen</option>
          <option value="Interview">Interview</option>
          <option value="Abgelehnt">Abgelehnt</option>
        </select>
      </div>
      <div>
        <label>Kontaktperson:</label>
        <input
          type="text"
          name="contact_person"
          value={formData.contact_person}
          onChange={handleChange}
        />
      </div>
      <div>
        <label>Email:</label>
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
        />
      </div>
      <div>
        <label>Telefon:</label>
        <input
          type="tel"
          name="phone"
          value={formData.phone}
          onChange={handleChange}
        />
      </div>
      <div>
        <label>Datum der Bewerbung:</label>
        <input
          type="date"
          name="date_applied"
          value={formData.date_applied}
          onChange={handleChange}
        />
      </div>
      <div>
        <label>Notizen:</label>
        <textarea
          name="notes"
          value={formData.notes}
          onChange={handleChange}
        />
      </div>
      <div>
        <label>Anschreiben:</label>
        <input
          type="file"
          name="coverLetter"
          onChange={handleFileChange}
          accept=".pdf,.docx,.txt"
        />
      </div>
      <div>
        <label>Stellenausschreibung:</label>
        <input
          type="file"
          name="jobPosting"
          onChange={handleFileChange}
          accept="image/*,application/pdf"
        />
      </div>
      <button type="submit">Bewerbung einreichen</button>
    </form>
  );
};

export default ApplicationForm;
