import React, { useEffect, useState } from 'react';
import axios from 'axios';
import UploadFile from './UploadFile';

const ApplicationList = () => {
  const [applications, setApplications] = useState([]);
  const [error, setError] = useState(null);

  // Daten von Backend abrufen
  useEffect(() => {
    axios
      .get('http://localhost:3001/applications') // Keine Paginierung mehr
      .then((response) => {
        console.log('Bewerbungen abgerufen:', response.data);
        setApplications(response.data.data); // Bewerbungen in den State setzen
      })
      .catch((err) => {
        setError(err.message);
      });
  }, []);

  return (
    <div>
      <h1>Bewerbungsliste</h1>
      <table>
        <thead>
          <tr>
            <th>Firma</th>
            <th>Position</th>
            <th>Status</th>
            <th>Anschreiben</th>
            <th>Datei hochladen</th>
          </tr>
        </thead>
        <tbody>
          {applications.map((app) => (
            <tr key={app.id}>
              <td>{app.company}</td>
              <td>{app.position}</td>
              <td>{app.status}</td>
              <td>
                {app.cover_letter_path ? (
                  <a
                    href={`http://localhost:3001/uploads/${app.cover_letter_path.split('/').pop()}`}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    Anschreiben herunterladen
                  </a>
                ) : (
                  'Kein Anschreiben'
                )}
              </td>
              <td>
                <UploadFile
                  applicationId={app.id}
                  onSuccess={() => window.location.reload()} // Aktualisiert die Liste nach dem Upload
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ApplicationList;
