import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ApplicationList = () => {
  const [applications, setApplications] = useState([]);
  const [error, setError] = useState(null);

  // Daten von Backend abrufen
  useEffect(() => {
    axios
      .get('http://localhost:3001/applications') // Alle Bewerbungen ohne Paginierung abrufen
      .then((response) => {
        setApplications(response.data.data); // Bewerbungen in den State setzen
      })
      .catch((err) => {
        setError(err.message);
      });
  }, []);

  // Bewerbung löschen
  const handleDelete = (id) => {
    axios
      .delete(`http://localhost:3001/applications/${id}`)
      .then((response) => {
        console.log('Antwort beim Löschen:', response); // Überprüfe die gesamte Antwort

        if (response.data.changes === 1) {
          alert('Bewerbung erfolgreich gelöscht!'); // Bestätigung, wenn eine Bewerbung gelöscht wurde
        } else {
          alert('Keine Bewerbung mit dieser ID gefunden oder bereits gelöscht.'); // Keine Änderungen gefunden
        }

        setApplications(applications.filter((app) => app.id !== id)); // Bewerbung aus dem State entfernen
      })
      .catch((err) => {
        console.error('Fehler beim Löschen:', err);
        alert('Fehler beim Löschen der Bewerbung: ' + err.message);
      });
  };
  if (error) {
    return <div>Fehler: {error}</div>;
  }

  return (
    <div>
      <h1>Bewerbungsliste</h1>
      <table>
        <thead>
          <tr>
            <th>Firma</th>
            <th>Position</th>
            <th>Status</th>
            <th>Anschreiben</th>
            <th>Aktionen</th> {/* Neue Spalte für den Lösch-Button */}
          </tr>
        </thead>
        <tbody>
          {applications.length === 0 ? (
            <tr>
              <td colSpan="5">Keine Bewerbungen vorhanden</td>
            </tr>
          ) : (
            applications.map((app) => (
              <tr key={app.id}>
                <td>{app.company}</td>
                <td>{app.position}</td>
                <td>{app.status}</td>
                <td>
                  {app.cover_letter_path ? (
                    <a
                      href={`http://localhost:3001/uploads/${app.cover_letter_path.split('/').pop()}`}
                      target="_blank"
                      rel="noopener noreferrer"
                    >
                      Anschreiben herunterladen
                    </a>
                  ) : (
                    'Kein Anschreiben'
                  )}
                </td>
                <td>
                  <button onClick={() => handleDelete(app.id)}>Löschen</button> {/* Lösch-Button */}
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default ApplicationList;
